        state = #{state},


        batch_no = #{valBatchNo},


        alter_time = #{alterTime},


        bank_card = #{bank_card},


        account_bank = #{account_bank},



    1=1

        and order_id=#{order_id}


        and batch_No=#{batchNo}


        and id=#{id}

mapper.select:
select

from mag_order o


    and state = #{state}


    and commodity_state = #{commodityState}


    and CUSTOMER_NAME like '%${customerName}%'


    and TEL like '%${tel}%'


    and order_no like '%${orderNo}%'


    and CREAT_TIME >= #{beginTime}


    and CREAT_TIME <= #{endTime}


    and order_type = #{orderType}


    and ALTER_TIME <= #{editEndTime}


    and (select count(1) as num from mag_servicepag_order s where o.id = s.order_id and s.state=1) != 0


order by CREAT_TIME desc

