      TEL AS tel , product_name_name AS productName , applay_money AS applayMoney ,loan_amount as loanAmount,
  PERIODS AS periods , applay_time AS applayTime , Order_state AS orderState, asset_state AS assetState
FROM mag_order

    1=1

        and CUSTOMER_NAME like '%${customerName}%'



        and Order_state=#{condition}



        and applay_time >= #{beginTime}


        and applay_time <= #{endTime}



order by applay_time desc

SELECT ID AS orderId , order_no AS orderNo ,  CUSTOMER_ID AS customerId , CUSTOMER_NAME AS customerName , CARD AS card ,
TEL AS tel , product_name_name AS productName , applay_money AS applayMoney ,
PERIODS AS periods , applay_time AS applayTime , Order_state AS orderState
FROM mag_order

    Order_state = 2

        and CUSTOMER_NAME like '%${customerName}%'



        and Order_state=#{condition}



        and CREAT_TIME >= #{beginTime}


        and CREAT_TIME <= #{endTime}



order by CREAT_TIME desc

SELECT magorder.ID AS orderId, order_no AS orderNo, customer_id AS customerId, CUSTOMER_NAME AS customerName, magorder.CARD AS card, magorder.TEL AS tel, product_name_name AS productName, loan_amount AS loanAmount, magcu.surplus_contract_amount AS surplusContractAmount, PERIODS AS periods, Examine_time AS examineTime, Order_state AS orderState, contract_amount AS contractAmount FROM mag_order magorder LEFT JOIN mag_customer magcu ON magcu.id = customer_id
Order_state = 4
and magorder.CUSTOMER_NAME like '%${customerName}%'
and magorder.Order_state=#{condition}
and magorder.CREAT_TIME >= #{beginTime}
and magorder.CREAT_TIME <= #{endTime}
order by magorder.CREAT_TIME desc
